import React from "react";
import CssBaseline from "@material-ui/core/CssBaseline";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/styles";

const styles = theme => ({
  root: {
    display: "flex",
    height: "100vh",
    minHeight: "100vh",
    alignItems: "center",
    width: "100%",
    background: "#3df0b1"
  },
  title: {
    fontSize: "56px",
    fontFamily: "lato",
    fontWeight: "bold",
    color: "#F5F5F5"
  },
  text: {
    fontSize: "20px",
    fontFamily: "lato",
    display: "flex",
    justifyContent: "center",
    textAlign: "center",
    color: "#1D2A30"
  },
  img: {
    height: "180px",
    width: "180px",
    borderRadius: "50%"
  },
  description: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center"
  }
});

class Home extends React.Component {
  render() {
    const { classes } = this.props;

    return (
      <div className={classes.root} id="sobre">
        <Grid container component="main" justify="space-evenly" xs={12}>
          <CssBaseline />
          <Grid item className={classes.description}>
            <Paper>
              <img
                className={classes.img}
                src={window.location.origin + "/stitch.jpg"}
              />
              <Typography className={classes.text}>
                Olá, me chamo Henrique e sou engenheiro da computação no ITA.{" "}
                {<br />}
                Sempre tive paixão por inovar e experimentar novas tecnologias.{" "}
                {<br />}
                Iniciei minha carreira acadêmica adentrando em um grupo de{" "}
                {<br />}
                pesquisa em robótica, no qual participei de várias competições{" "}
                {<br />}
                de cunho nacional e internacional, tal como a Robocup. Após{" "}
                {<br />}
                isso, comecei a desenvolver projetos pessoais e adentrar no{" "}
                {<br />}
                mundo profissional, no qual acabei adentrando através da startup{" "}
                {<br />}
                Quero Educação, na qual tive experiência com Data Science,{" "}
                {<br />}
                principalmente com processamento de linguagem natural.
              </Typography>
            </Paper>
          </Grid>

          <Grid item className={classes.description}>
            <img
              className={classes.img}
              src={window.location.origin + "/stitch.jpg"}
            />
          </Grid>
          <Grid item className={classes.description}>
            <img
              className={classes.img}
              src={window.location.origin + "/stitch.jpg"}
            />
          </Grid>
        </Grid>
      </div>
    );
  }
}

Home.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(Home);
