  img: {
    height: "180px",
    width: "180px",
    borderRadius: "50%"
  },
  description: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center"
  },
  paper: {
    align: "center",
    justifyContent: "center"
  }
});

class Home extends React.Component {
  render() {
    const { classes } = this.props;

    return (
      <div className={classes.root} id="sobre">
        <Grid container component="main" justify="space-evenly" xs={12}>
          <CssBaseline />
